CREATE TABLE submission(submission_id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,submission_title VARCHAR(255),submission_authors INT UNSIGNED,submission_file VARCHAR(255),submission_decission INT,submission_upload_time TIMESTAMP,is_delete INT,FOREIGN KEY(submission_authors) REFERENCES authors(authors_id) ON UPDATE CASCADE ON DELETE CASCADE ); CREATE TABLE periode(periode_id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,periode_name VARCHAR(255),periode_theme VARCHAR(255),periode_start DATE,periode_finish DATE,is_active INT,is_delete INT); CREATE TABLE base_settings(base_id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,base_company_name VARCHAR(255),base_dashboard_color VARCHAR(255),base_contact_us VARCHAR(255),is_active INT,is_delete INT); CREATE TABLE front_settings(front_id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,front_title VARCHAR(255),front_subtitle VARCHAR(255),front_about VARCHAR(255),front_color VARCHAR(255),is_active INT,is_delete INT); CREATE TABLE front_content(front_content_id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,front_content_title VARCHAR(255),front_content_content VARCHAR(255),front_content_add_time TIMESTAMP,is_active INT,is_delete INT); CREATE TABLE speakers(speakers_id INT UNSIGNED AUTO_INCREMENT PRIMARY KEY,speakers_name VARCHAR(255),speakers_job VARCHAR(255),speakers_image VARCHAR(255),speakers_add_time TIMESTAMP,is_active INT,is_delete INT);
